CREATE TABLE IF NOT EXISTS "Indexers" ("Id" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, "Name" TEXT NOT NULL, "Priority" INTEGER NOT NULL DEFAULT 25, "URI" TEXT NOT NULL, "apiKey" TEXT);
CREATE TABLE IF NOT EXISTS "QualityDefinitions" ("Id" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, "QualityId" INTEGER, "Name" TEXT NOT NULL, "PreferredSize" INTEGER NOT NULL, "MinSize" INTEGER NOT NULL, "MaxSize" INTEGER NOT NULL);
CREATE UNIQUE INDEX IF NOT EXISTS "IX_Indexers_Name" ON "Indexers" ("Name" ASC);